// AuctionApp.js - Main auction frontend component

import React, { useState, useEffect, useRef } from 'react';
import AuctionList from './AuctionList';
import AuctionDetails from './AuctionDetails';
import AuctionCreate from './AuctionCreate';
import NotificationBar from './NotificationBar';

function AuctionApp() {
  const [selectedAuction, setSelectedAuction] = useState(null);
  const [refresh, setRefresh] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const wsRef = useRef(null);

  useEffect(() => {
    wsRef.current = new window.WebSocket('ws://localhost:4000');

    wsRef.current.onmessage = (event) => {
      try {
        const msg = JSON.parse(event.data);

        if (msg.type === 'bid') {
          setNotifications((n) => [
            `New bid: ${msg.amount} by ${msg.userId} on auction ${msg.auctionId}`,
            ...n,
          ]);
        }

        if (msg.type === 'outbid') {
          setNotifications((n) => [
            `You have been outbid on auction ${msg.auctionId}`,
            ...n,
          ]);
        }

        if (msg.type === 'auction-ended') {
          setNotifications((n) => [
            `Auction ended. Highest bid: ${msg.highestBid}`,
            ...n,
          ]);
        }

        if (msg.type === 'counter-offer') {
          setNotifications((n) => [
            `Counter-offer received: ${msg.amount} on auction ${msg.auctionId}`,
            ...n,
          ]);
        }

        if (msg.type === 'bid-accepted') {
          setNotifications((n) => [
            `Your bid was accepted on auction ${msg.auctionId}`,
            ...n,
          ]);
        }

        if (msg.type === 'bid-rejected') {
          setNotifications((n) => [
            `Your bid was rejected on auction ${msg.auctionId}`,
            ...n,
          ]);
        }
      } catch (err) {
        console.error("WebSocket message parse error:", err);
      }
    };

    // cleanup when component unmounts
    return () => {
      if (wsRef.current) {
        wsRef.current.close();
      }
    };
  }, []);

  const clearNotifications = () => setNotifications([]);

  return (
    <div
      style={{
        fontFamily: 'sans-serif',
        padding: 32,
        maxWidth: 600,
        margin: 'auto',
      }}
    >

<div style={{ marginBottom: 16 }}>
  <label>User ID: <input value={userId} onChange={e => setUserId(e.target.value)} placeholder="Enter user ID" /></label>
  <label style={{ marginLeft: 16 }}>Role:
    <select value={role} onChange={e => setRole(e.target.value)}>
      <option value="buyer">Buyer</option>
      <option value="seller">Seller</option>
    </select>
  </label>
</div>

      <h1>Mini Auction Platform</h1>

      <NotificationBar
        notifications={notifications}
        onClear={clearNotifications}
      />

      {!selectedAuction ? (
        <>
          <AuctionCreate onCreated={() => setRefresh((r) => !r)} />
          <AuctionList onSelectAuction={setSelectedAuction} key={refresh} />
        </>
      ) : (
        <AuctionDetails
          auction={selectedAuction}
          onBack={() => setSelectedAuction(null)}
        />
      )}
    </div>
  );
}

export default AuctionApp;
