// AuctionList.js - Displays all auctions
import React, { useEffect, useState } from 'react';

function AuctionList({ onSelectAuction }) {
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:4000/api/auctions')
      .then(res => res.json())
      .then(data => {
        setAuctions(data.auctions || []);
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Loading auctions...</div>;
  if (!auctions.length) return <div>No auctions found.</div>;

  return (
    <div>
      <h2>Available Auctions</h2>
      <ul>
        {auctions.map(auction => (
          <li key={auction.id}>
            <button onClick={() => onSelectAuction(auction)}>
              {auction.title} (Ends: {new Date(auction.end_time).toLocaleString()})
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AuctionList;
