// AuctionDetails.js - Shows details and bids for a selected auction
import React, { useEffect, useState } from 'react';

function AuctionDetails({ auction, onBack }) {
  const [bids, setBids] = useState([]);
  const [amount, setAmount] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetch(`http://localhost:4000/api/auction/${auction.id}/bids`)
      .then(res => res.json())
      .then(data => {
        setBids(data.bids || []);
        setLoading(false);
      });
  }, [auction.id]);

  const placeBid = async () => {
    setError('');
    const res = await fetch(`http://localhost:4000/api/auction/${auction.id}/bid`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userId: 'demo-user', amount: Number(amount) })
    });
    const data = await res.json();
    if (res.ok) {
      setBids([...bids, data.bid]);
      setAmount('');
    } else {
      setError(data.error || 'Bid failed');
    }
  };

  return (
    <div>
      <button onClick={onBack}>Back to Auctions</button>
      <h2>{auction.title}</h2>
      <p>{auction.description}</p>
      <p>Starting Bid: {auction.starting_bid}</p>
      <p>Ends: {new Date(auction.end_time).toLocaleString()}</p>
      <h3>Bids</h3>
      {loading ? <div>Loading bids...</div> : (
        <ul>
          {bids.map(bid => (
            <li key={bid.id}>{bid.user_id}: {bid.amount} @ {new Date(bid.created_at).toLocaleString()}</li>
          ))}
        </ul>
      )}
      <div>
        <input
          type="number"
          value={amount}
          onChange={e => setAmount(e.target.value)}
          placeholder="Your bid amount"
        />
        <button onClick={placeBid} disabled={!amount}>Place Bid</button>
        {error && <div style={{ color: 'red' }}>{error}</div>}
      </div>
    </div>
  );
}

export default AuctionDetails;
