// AuctionApp.js - Main auction frontend component

import React, { useState } from 'react';

import AuctionList from './AuctionList';
import AuctionDetails from './AuctionDetails';
import AuctionCreate from './AuctionCreate';


function AuctionApp() {
  const [selectedAuction, setSelectedAuction] = useState(null);
  const [refresh, setRefresh] = useState(false);

  return (
    <div style={{ fontFamily: 'sans-serif', padding: 32, maxWidth: 600, margin: 'auto' }}>
      <h1>Mini Auction Platform</h1>
      {!selectedAuction ? (
        <>
          <AuctionCreate onCreated={() => setRefresh(r => !r)} />
          <AuctionList onSelectAuction={setSelectedAuction} key={refresh} />
        </>
      ) : (
        <AuctionDetails auction={selectedAuction} onBack={() => setSelectedAuction(null)} />
      )}
    </div>
  );
}

export default AuctionApp;
