// AuctionDetails.js - Shows details and bids for a selected auction

import React, { useEffect, useState } from 'react';
import { useUser } from './UserContext';


function AuctionDetails({ auction, onBack }) {
  const { userId, role } = useUser();
  const [bids, setBids] = useState([]);
  const [amount, setAmount] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [counterAmount, setCounterAmount] = useState('');
  const [actionMsg, setActionMsg] = useState('');

  useEffect(() => {
    fetch(`http://localhost:4000/api/auction/${auction.id}/bids`)
      .then(res => res.json())
      .then(data => {
        setBids(data.bids || []);
        setLoading(false);
      });
  }, [auction.id]);

  const placeBid = async () => {
    setError('');
    const res = await fetch(`http://localhost:4000/api/auction/${auction.id}/bid`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userId, amount: Number(amount) })
    });
    const data = await res.json();
    if (res.ok) {
      setBids([...bids, data.bid]);
      setAmount('');
    } else {
      setError(data.error || 'Bid failed');
    }
  };

  // Seller actions
  const acceptBid = async () => {
    setActionMsg('');
    const res = await fetch(`http://localhost:4000/api/auction/${auction.id}/accept`, { method: 'POST' });
    const data = await res.json();
    if (res.ok) setActionMsg('Bid accepted!');
    else setActionMsg(data.error || 'Accept failed');
  };
  const rejectBid = async () => {
    setActionMsg('');
    const res = await fetch(`http://localhost:4000/api/auction/${auction.id}/reject`, { method: 'POST' });
    const data = await res.json();
    if (res.ok) setActionMsg('Bid rejected!');
    else setActionMsg(data.error || 'Reject failed');
  };
  const sendCounterOffer = async () => {
    setActionMsg('');
    const res = await fetch(`http://localhost:4000/api/auction/${auction.id}/counter`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ counterAmount: Number(counterAmount) })
    });
    const data = await res.json();
    if (res.ok) setActionMsg('Counter-offer sent!');
    else setActionMsg(data.error || 'Counter-offer failed');
  };

  return (
    <div>
      <button onClick={onBack}>Back to Auctions</button>
      <h2>{auction.title}</h2>
      <p>{auction.description}</p>
      <p>Starting Bid: {auction.starting_bid}</p>
      <p>Bid Increment: {auction.bid_increment}</p>
      <p>Go Live: {auction.go_live ? new Date(auction.go_live).toLocaleString() : ''}</p>
      <p>Duration: {auction.duration} minutes</p>
      <p>Status: {auction.status}</p>
      <p>Ends: {new Date(auction.end_time).toLocaleString()}</p>
      <h3>Bids</h3>
      {loading ? <div>Loading bids...</div> : (
        <ul>
          {bids.map(bid => (
            <li key={bid.id}>{bid.user_id}: {bid.amount} @ {new Date(bid.created_at).toLocaleString()}</li>
          ))}
        </ul>
      )}
      <div>
        <input
          type="number"
          value={amount}
          onChange={e => setAmount(e.target.value)}
          placeholder="Your bid amount"
        />
        <button onClick={placeBid} disabled={!amount || !userId}>Place Bid</button>
        {error && <div style={{ color: 'red' }}>{error}</div>}
      </div>
      {/* Seller actions: only show if logged-in user is auction owner and role is seller */}
      {role === 'seller' && userId === auction.owner && (
        <div style={{ marginTop: 24 }}>
          <h4>Seller Actions</h4>
          <button onClick={acceptBid}>Accept Highest Bid</button>
          <button onClick={rejectBid} style={{ marginLeft: 8 }}>Reject Highest Bid</button>
          <div style={{ marginTop: 8 }}>
            <input
              type="number"
              value={counterAmount}
              onChange={e => setCounterAmount(e.target.value)}
              placeholder="Counter-offer amount"
            />
            <button onClick={sendCounterOffer} disabled={!counterAmount} style={{ marginLeft: 8 }}>Send Counter-Offer</button>
          </div>
          {actionMsg && <div style={{ color: 'green', marginTop: 8 }}>{actionMsg}</div>}
        </div>
      )}
    </div>
  );
}

export default AuctionDetails;
